// Este es el c√≥digo completo para el Nodo de Funci√≥n.

// Paso 1: Verificar que recibimos un mensaje de Telegram con texto.
const message = $json.message;
if (!message || !message.text) {
  // Si no es un mensaje con texto, detenemos el flujo aqu√≠.
  return null;
}

const text = message.text;

// Paso 2: Usar una expresi√≥n regular para ver si el texto coincide con nuestro comando.
// Buscamos el patr√≥n: /dieta rut [ALGO] objetivo [OTRA COSA]
const match = text.match(/\/dieta rut (.+) objetivo (.+)/i);

// Paso 3: Si el texto del mensaje S√ç coincide con el patr√≥n...
if (match) {
  // Extraemos las partes que nos interesan (el RUT y el objetivo).
  const [, rutDelUsuario, objetivo] = match;

  // Paso 4: Limpiar el RUT para que sea solo num√©rico.
  // IMPORTANTE: Este m√©todo asume que la columna 'rut' en Supabase es de tipo num√©rico
  // y fallar√° si el RUT del usuario termina en 'K'.
  const rutLimpio = rutDelUsuario.replace(/[.-]/g, '');
  const rutNumerico = parseInt(rutLimpio, 10);

  // Verificamos si la limpieza y conversi√≥n a n√∫mero fue exitosa.
  if (isNaN(rutNumerico)) {
    // Si no es un n√∫mero v√°lido (ej. por la 'K'), detenemos el flujo.
    return null;
  }

  // Paso 5: Devolvemos los datos ya limpios y listos para los siguientes nodos.
  return {
    rut: rutNumerico,
    objetivo: objetivo.trim(),
    chat_id: message.chat.id
  };
}

// Paso 6: Si el texto del mensaje NO coincide con el patr√≥n, detenemos el flujo.
return null;

// Este es el c√≥digo completo para el Nodo de Funci√≥n.

// Paso 1: Obtener los datos del paciente y el objetivo de los nodos anteriores.
// Los datos del paciente vienen del nodo justo anterior (Supabase: Get Patient).
const paciente = $json;

// El objetivo viene del nodo "Parse Command", as√≠ que lo referenciamos por su nombre.
const objetivo = $('Code').item.json.objetivo;


// Paso 2: Calcular la Tasa Metab√≥lica Basal (BMR) usando la f√≥rmula de Harris-Benedict.
let bmr;
if (paciente.genero.toLowerCase() === 'masculino') {
  bmr = 88.362 + (13.397 * paciente.peso_kg) + (4.799 * paciente.altura_cm) - (5.677 * paciente.edad);
} else { // Asumimos Femenino
  bmr = 447.593 + (9.247 * paciente.peso_kg) + (3.098 * paciente.altura_cm) - (4.330 * paciente.edad);
}

// Paso 3: Calcular el Gasto Energ√©tico Diario Total (TDEE).
// Se multiplica el BMR por un factor de actividad. Usaremos 1.2 (sedentario) como base.
// NOTA: Este factor se puede hacer m√°s complejo si en el futuro se a√±ade el nivel de actividad del paciente.
const tdee = bmr * 1.2;


// Paso 4: Ajustar las calor√≠as seg√∫n el objetivo del paciente.
let caloriasObjetivo;
switch (objetivo.toLowerCase()) {
  case 'perder peso':
    caloriasObjetivo = tdee - 500; // D√©ficit cal√≥rico
    break;
  case 'ganar musculo':
    caloriasObjetivo = tdee + 500; // Super√°vit cal√≥rico
    break;
  case 'mantener':
  default:
    caloriasObjetivo = tdee; // Mantener calor√≠as
}

// Paso 5: Distribuir las calor√≠as en macronutrientes (ej. 40% Carbs, 30% Prote√≠nas, 30% Grasas).
const macros = {
  // 1g de prote√≠na = 4 kcal
  proteinas: Math.round((caloriasObjetivo * 0.30) / 4),
  // 1g de carbohidratos = 4 kcal
  carbohidratos: Math.round((caloriasObjetivo * 0.40) / 4),
  // 1g de grasa = 9 kcal
  grasas: Math.round((caloriasObjetivo * 0.30) / 9)
};


// Paso 6: Devolver todos los c√°lculos en un objeto estructurado para el siguiente nodo (OpenAI).
return {
  paciente_id: paciente.id,
  // Asumimos que el nutricionista es el 'usuario_id' asociado al paciente.
  creada_por: paciente.usuario_id,
  calorias_objetivo: Math.round(caloriasObjetivo),
  macros: macros,
  objetivo: objetivo
};

